import styled from 'styled-components';

const Screen = styled.div`
    position:fixed;
    width:100%;
    height: 100%;
    background: #8C11BE;
    padding: 25px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
`;

export default Screen;
